        });
        break;

      case "ai":
        resources.push({
          title: "AI Integration Guide",
          url: `${baseUrl}/ai/setup`,
        });
        break;
    }

    // Add error-type specific resources
    if (error.type === "type-error") {
      resources.push({
        title: "TypeScript Type System Guide",
        url: `${baseUrl}/typescript/types`,
