// Do not edit this file manually

${hooks
  .map(
    (hook) => `
export { ${hook.name} } from '${hook.path}';
`
  )
  .join("")}

export const PluginHooks = {
${hooks.map((hook) => `  ${hook.name},`).join("\n")}
};
`;

    await fs.writeFile(
      path.join(this.context.paths.frontend, "src/plugins/hooks/index.ts"),
      indexContent
    );
  }
