    type: 'codemod',
    automated: true,
    description: 'Update import paths for FARM 2.0',
    filePattern: '**/*.{ts,tsx,js,jsx}',
    transform: (content: string) => {
      return content
        .replace(/from ['"]@farm\/([^'"]+)['"]/g, 'from "@farm/core/$1"')
        .replace(/import.*@farm\/ai-hooks.*/g, 'import { useAI } from "@farm/hooks";');
    }
  }
];
